import CampoTexto from '../CampoTexto'
import './Formulario.css'
import ListaSuspensa from '../ListaSuspensa';
import Botao from '../Botao';
import { useState } from 'react'

const times = [
  'Programação',
  'Front-End',
  'Back-End',
  'Data Science',
  'Devops',
  'UX e Design',
  'Mobile',
  'Inovação',
  'Gestão'
]

// import CampoTexto from './componentes/CampoTexto';


/** criado a função que ao clicar np botão criar card*/
const aoSalvar = (evento) => 
{
  evento.preventDefault();
  console.log('cliquei no botão criar card'); 
  console.log(evento.target); 
}

/** criado a função */
const Formulario = () => 
{

    /** hooks onde é definido o estado do valor e set(como alterar) do valor */
    const [nome, setNome] = useState('')  /** ao mudar o estado, o react renderiza novamente */
    const [cargo, setCargo] = useState('')  /** ao mudar o estado, o react renderiza novamente */
    const [imagem, setImagem] = useState('')  /** ao mudar o estado, o react renderiza novamente */


    return ( /** return JSX */
           <section className='formulario'>
                <form onSubmit={aoSalvar}>
                    <h2>Preencha os dados para criar o card do colaborador(a)</h2>
                    <CampoTexto obrigatorio={true} label="Nome" placeholder="Digite o seu nome" type="text" valor={nome} aoAlterado={valor => setNome(valor)}/>
                    <CampoTexto obrigatorio={true} label="Cargo" placeholder="Digite o seu cargo" type="text" valor={cargo}/>
                    <CampoTexto label="Imagem" placeholder="Informe o endereço da imagem" type="text" valor={imagem}/>
                    <ListaSuspensa obrigatorio={true} label="Time" itens={times}/>
                    <Botao>Criar Card</Botao>
                </form>
           </section>
        )
}

/** exportado a função */
export default Formulario
