import './index.css'


/** criado a função */
const CampoTexto = ({ title }) => 
{
    // let title = 'Nome'

    return ( /** return JSX */
            <div className="campo-texto">
                <label>{title}</label>
                <input placeholder={`Digite seu ${title.toLowerCase()}`} type="text" name="nome" id="name"/>
            </div>
        )
}

/** exportado a função */
export default CampoTexto
