import './ListaSuspensa.css'

/** criado a função */
const ListaSuspensa = (props) => 
{
    // console.log(props.itens)

    return (
        /* return JSX */
        <div className='lista-suspensa'>
          <label>{props.label}</label>
          <div className="label-fontawesome">
            <select required={props.obrigatorio} onChange={evento => props.aoAlterado(evento.target.value)} className="select-com-padding"> 
              {props.itens.map(item => {
                return <option key={item} value={item}>{item}</option> // Added value={item} to the option
              })}
            </select>
            <span className="span-fontawesome">
              {props.iconeLabel}
            </span>
          </div>
        </div>
      )

}

/** exportado a função */
export default ListaSuspensa
