import './index.css'


/** criado a função */
const CampoTexto = (props) => 
{
    let title = "nome"
    let type = "text"
    console.log(props)
    return ( /** return JSX */
            <div className="campo-texto">
                <label>{title}</label>
                <input placeholder={`Digite seu ${title.toLowerCase()}`} type={`${type}`} 
                name={`${title.toLowerCase()}`} id={`${title.toLowerCase()}`} />
            </div>
        )
}

/** exportado a função */
export default CampoTexto
