import './CampoTexto.css'


/** criado a função */
const CampoTexto = (props) => 
{
    return ( /** return JSX */
            <div className="campo-texto">
                <label>{props.label}</label>
                <input required={props.obrigatorio} placeholder={props.placeholder} type={props.type} 
                name={props.label.toLowerCase()} id={props.label.toLowerCase()} />
            </div>
        )
}

/** exportado a função */
export default CampoTexto

