import './ListaSuspensa.css'

/** criado a função */
const ListaSuspensa = (props) => 
{
    // console.log(props.itens)

    return ( /** return JSX */
            <div className='lista-suspensa'>
                <label>{props.label}</label>
                <div className="lista-suspensa-fontawesome">
                    <select className="lista-suspensa-select" required={props.obrigatorio} onChange={evento => props.aoAlterado(evento.target.value)}>
                    <option  selected={props.selected}>Selecione</option> 
                        {props.itens.map(item => {
                            return <option value={props.valor} key={item}>{item}</option>
                        })}
                    </select>
                    <span className="lista-suspensa-fontawesome-span">
                        {props.iconeLabel}
                    </span>
                </div>
            </div>
        )
}

/** exportado a função */
export default ListaSuspensa
