/** criado a função que ao clicar np botão criar card*/
const aoSalvar = (evento) => 
{
  evento.preventDefault();
  console.log('cliquei no botão criar card'); 
  console.log(evento.target.label); 
}

/** criado a função */
const Formulario = () => 
{
    return ( /** return JSX */
           <section className='formulario'>
                <form onSubmit={aoSalvar}>
                    <h2>Preencha os dados para criar o card do colaborador(a)</h2>
                    <CampoTexto obrigatorio={true} label="Nome" placeholder="Digite o seu nome" type="text"/>
                    <CampoTexto obrigatorio={true} label="Cargo" placeholder="Digite o seu cargo" type="text"/>
                    <CampoTexto label="Imagem" placeholder="Informe o endereço da imagem" type="text"/>
                    <ListaSuspensa obrigatorio={true} label="Time" itens={times}/>
                    <Botao>
                      Criar Card
                    </Botao>
                </form>
           </section>
        )
}

/** exportado a função */
export default Formulario
