import { useState } from 'react'
import './CampoTexto.css'

// let valor = 'Teste'
const [valor, setValor] = useState('')

/** criado a função que ao mudar no input*/
const aoDigitado = (evento) => 
{
    setValor(evento.target.value)
    console.log(valor)
}


/** criado a função */
const CampoTexto = (props) => 
{
    return ( /** return JSX */
            <div className="campo-texto">
                <label>{props.label}</label>
                <input required={props.obrigatorio} placeholder={props.placeholder} type={props.type} value={valor}
                    onChange={aoDigitado} name={props.label.toLowerCase()} id={props.label.toLowerCase()} />
            </div>
        )
}

/** exportado a função */
export default CampoTexto

